/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `trg_update_student_fee_after_payment_update` AFTER UPDATE ON `fee_payments` FOR EACH ROW BEGIN
    DECLARE amount_change DECIMAL(10,2);
    DECLARE new_amount_paid DECIMAL(10,2);
    
    -- Calculate the net change in payment amount
    IF OLD.is_active = TRUE AND NEW.is_active = FALSE THEN
        SET amount_change = -OLD.amount_paid;
    ELSEIF OLD.is_active = FALSE AND NEW.is_active = TRUE THEN
        SET amount_change = NEW.amount_paid;
    ELSEIF OLD.is_active = TRUE AND NEW.is_active = TRUE THEN
        SET amount_change = NEW.amount_paid - OLD.amount_paid;
    ELSE
        SET amount_change = 0;
    END IF;
    
    IF amount_change != 0 THEN
        -- Get current amount_paid to calculate new total
        SELECT amount_paid INTO new_amount_paid 
        FROM student_fees 
        WHERE id = NEW.student_fee_id;
        
        SET new_amount_paid = new_amount_paid + amount_change;
        
        UPDATE student_fees 
        SET amount_paid = new_amount_paid,
            status = CASE
                WHEN new_amount_paid >= final_amount THEN 'Paid'
                WHEN new_amount_paid > 0 THEN 'Partially Paid'
                ELSE 'Pending'
            END,
            updated_at = CURRENT_TIMESTAMP
        WHERE id = NEW.student_fee_id;
    END IF;
END */;;
